export const string = (value: unknown): value is string => typeof value === 'string'

// eslint-disable-next-line @typescript-eslint/ban-types
export const object = (value: unknown): value is object =>
  value != null && typeof value === 'object'

// eslint-disable-next-line @typescript-eslint/ban-types
const fn = <T extends Function>(value: unknown): value is T => typeof value === 'function'

export { fn as function }
import type { Hasher, Falsy } from '../types'

interface Includes {
  (value: string, search: string): boolean
  <T>(value: readonly T[], search: T): boolean
}

export const includes: Includes = (value: string | readonly unknown[], search: unknown) =>
  // eslint-disable-next-line no-implicit-coercion
  !!~(value as string).indexOf(search as string)

export const join = (parts: readonly string[], separator = '-'): string => parts.join(separator)

export const joinTruthy = (parts: readonly (string | Falsy)[], separator?: string): string =>
  join(parts.filter(Boolean) as string[], separator)

export const tail = <T extends string | readonly unknown[]>(array: T, startIndex = 1): T =>
  array.slice(startIndex) as T

export const identity = <T>(value: T): T => value

export const noop = (): void => {
  /* no-op */
}

export const capitalize = (value: string): string => value[0].toUpperCase() + tail(value)

export const hyphenate = (value: string): string => value.replace(/[A-Z]/g, '-$&').toLowerCase()

export const escape =
  (typeof CSS !== 'undefined' && CSS.escape) ||
  // Simplified: escaping only special characters
  // Needed for NodeJS and Edge <79 (https://caniuse.com/mdn-api_css_escape)
  ((className: string): string => {
    const firstCodeUnit = className.charCodeAt(0)
    let firstChar = ''

    // If the character is the first character and is in the range [0-9] (2xl, ...)
    if (firstCodeUnit >= 0x0030 && firstCodeUnit <= 0x0039) {
      // https://drafts.csswg.org/cssom/#escape-a-character-as-code-point
      firstChar = '\\' + firstCodeUnit.toString(16) + ' '
      className = tail(className)
    }

    // Simplifed escape testing only for chars that we know happen to be in tailwind directives
    return firstChar + className.replace(/[!./:#]/g, '\\$&')
  })

// Based on https://stackoverflow.com/a/52171480
export const cyrb32: Hasher = (value: string): string => {
  let h = 9

  for (let index = value.length; index--; ) {
    h = Math.imul(h ^ value.charCodeAt(index), 0x5f356495)
  }

  return 'tw-' + ((h ^ (h >>> 9)) >>> 0).toString(36)
}

/**
 * Find the array index of where to add an element to keep it sorted.
 *
 * @returns The insertion index
 */
export const sortedInsertionIndex = (array: readonly number[], element: number): number => {
  let high = array.length

  // Theres only one option then
  if (high === 0) return 0

  // Find position by binary search
  for (let low = 0; low < high; ) {
    const pivot = (high + low) >> 1

    // Less-Then-Equal to add new equal element after all existing equal elements (stable sort)
    if (array[pivot] <= element) {
      low = pivot + 1
    } else {
      high = pivot
    }
  }

  return high
}
/* eslint-disable @typescript-eslint/consistent-type-assertions */
import type { CSSRules } from '../types'

import { joinTruthy } from './util'

const positions = (resolve: (position: string) => undefined | string[] | void) => (
  value: string | string[] | undefined,
  position: string,
  prefix?: string,
  suffix?: string,
): CSSRules | undefined => {
  if (value) {
    const properties = position && resolve(position)

    if (properties && properties.length > 0) {
      return properties.reduce((declarations, property) => {
        declarations[joinTruthy([prefix, property, suffix])] = value
        return declarations
      }, {} as CSSRules)
    }
  }
}

export const corners = positions(
  (key) =>
    (({
      t: ['top-left', 'top-right'],
      r: ['top-right', 'bottom-right'],
      b: ['bottom-left', 'bottom-right'],
      l: ['bottom-left', 'top-left'],
      tl: ['top-left'],
      tr: ['top-right'],
      bl: ['bottom-left'],
      br: ['bottom-right'],
    } as Record<string, undefined | string[]>)[key]),
)

export const expandEdges = (key: string): string[] | undefined => {
  const parts = (({ x: 'lr', y: 'tb' } as Record<string, undefined | string>)[key] || key || '')
    .split('')
    .sort()

  for (let index = parts.length; index--; ) {
    if (
      !(parts[index] = ({
        t: 'top',
        r: 'right',
        b: 'bottom',
        l: 'left',
      } as Record<string, string>)[parts[index]])
    )
      return
  }

  if (parts.length) return parts
}

// Support several edges like 'tr'
// 'x' and 'y' can not be combined with others because size 'xl'
// Every char must be a edge position
// Sort to have consistent declaration ordering
export const edges = positions(expandEdges)
/* eslint-enable @typescript-eslint/consistent-type-assertions */
/* eslint-disable no-return-assign, no-cond-assign, @typescript-eslint/consistent-type-assertions */
import type {
  Theme,
  CSSRules,
  CSSProperties,
  DirectiveHandler,
  Plugins,
  ThemeResolver,
  Context,
  Falsy,
  ThemeContainer,
} from '../types'

import * as is from '../internal/is'

import { includes, join, joinTruthy, tail, capitalize } from './util'
import { corners, expandEdges, edges } from './helpers'

// Shared variables
let _: undefined | string | CSSRules | CSSProperties | string[] | boolean | Falsy | number
let __: undefined | string | CSSProperties
let $: undefined | string | number

const property = (property: string) => (
  params: string[],
  context: unknown,
  id: string,
): CSSRules => ({
  [property]: id + ((_ = join(params)) && '-' + _),
})

const propertyValue = (property: string, separator?: string) => (params: string[]): CSSRules => ({
  [property]: join(params, separator),
})

const themeProperty = (section?: keyof Theme): DirectiveHandler => (
  params: string[],
  { theme },
  id,
) => ({
  [section || id]: theme(section || (id as keyof Theme), params) as string,
})

const alias = (directive: DirectiveHandler, name: string): DirectiveHandler => (params, context) =>
  directive(params, context, name)

const display = property('display')
const position = property('position')
const textTransform = property('textTransform')
const textDecoration = property('textDecoration')
const fontStyle = property('fontStyle')
const fontVariantNumeric = (key: string): DirectiveHandler => (params, context, id) => ({
  ['--tw-' + key]: id,
  fontVariantNumeric:
    'var(--tw-ordinal,/*!*/ /*!*/) var(--tw-slashed-zero,/*!*/ /*!*/) var(--tw-numeric-figure,/*!*/ /*!*/) var(--tw-numeric-spacing,/*!*/ /*!*/) var(--tw-numeric-fraction,/*!*/ /*!*/)',
})

const inset: DirectiveHandler = (params, { theme }, id) => ({ [id]: theme('inset', params) })

const opacityProperty = (
  params: string[],
  theme: ThemeResolver,
  id: string,
  section = id,
): CSSRules => ({
  [`--tw-${id}-opacity`]: theme((section + 'Opacity') as 'textOpacity', tail(params)),
})

const parseColorComponent = (chars: string, factor: number): number =>
  Math.round(parseInt(chars, 16) * factor)

const asRGBA = <T extends string | undefined>(
  color: T,
  opacityProperty: string,
  opacityDefault?: string,
): T | string => {
  if (color && color[0] === '#') {
    return `rgba(${parseColorComponent(
      color.substr(1, (_ = (color.length - 1) / 3)),
      ($ = [17, 1, 0.062272][_ - 1]),
    )},${parseColorComponent(color.substr(1 + _, _), $)},${parseColorComponent(
      color.substr(1 + 2 * _, _),
      $,
    )},var(--tw-${opacityProperty}${opacityDefault ? ',' + opacityDefault : ''}))`
  }

  return color
}

const withOpacityFallback = (
  property: string,
  kind: string,
  color: string | undefined,
): CSSRules | undefined =>
  color && (_ = asRGBA(color, kind + '-opacity')) && _ !== color
    ? {
        [`--tw-${kind}-opacity`]: '1',
        [property]: [color, _],
      }
    : { [property]: color }

const reversableEdge = (
  params: string[],
  { theme }: Context,
  id: string,
  section: 'divideWidth' | 'space',
  prefix: string,
  suffix?: string,
  // eslint-disable-next-line max-params
): CSSRules | undefined =>
  (_ = ({ x: ['right', 'left'], y: ['bottom', 'top'] } as Record<string, undefined | string[]>)[
    params[0]
  ]) && ($ = `--tw-${id}-${params[0]}-reverse`)
    ? params[1] === 'reverse'
      ? {
          [$]: '1',
        }
      : {
          [$]: '0',
          [joinTruthy([prefix, _[0], suffix])]:
            (__ = theme(section, tail(params))) && `calc(${__} * var(${$}))`,
          // With fallback
