import type { Context, CSSRules, Prefixer, Rule } from '../types'

import * as is from '../internal/is'

import { join, includes, escape, hyphenate } from './util'
import {
  responsivePrecedence,
  declarationPropertyPrecedence,
  makeVariantPresedenceCalculator,
  atRulePresedence,
} from './presedence'

export interface RuleWithPresedence {
  r: string
  p: number
}

const stringifyBlock = (body: string, selector: string): string => selector + '{' + body + '}'

export const serialize = (
  prefix: Prefixer,
  variants: Record<string, string>,
  context: Context,
): ((css: CSSRules, className?: string, rule?: Rule) => RuleWithPresedence[]) => {
  const { theme, tag } = context

  // Hash/Tag tailwind custom properties during serialization
  // used by `tagVars` below
  const tagVar = (_: string, property: string): string => '--' + tag(property)

  const tagVars = (value: string | number): string => `${value}`.replace(/--(tw-[\w-]+)\b/g, tagVar)

  // Create a css declaration with prefix and hashed custom properties
  const stringifyDeclaration = (
    property: string,
    value: string | number | string[],
    important: boolean | undefined,
  ): string => {
    property = tagVars(property)

    // Support array fallbacks
    return Array.isArray(value)
      ? join(
          value.filter(Boolean).map((value) => prefix(property, tagVars(value), important)),
          ';',
        )
      : prefix(property, tagVars(value), important)
  }

  // List of css rule with presedence to be injected
  let rules: RuleWithPresedence[]

  // Responsible for converting the css into one or more css strings
  // which will be injected into the page
  const stringify = (
    // Upper at-rules, selctoer that should wrap each generated css block
    atRules: string[],
    // The current css selector
    selector: string,
    // The current presedence for determine the css position in the stylesheet
    presedence: number,
    // The rules object
    css: CSSRules,
    important: boolean | undefined,
  ): void => {
    // 1. Properties
    // 3. *
    // 2. @...
    // 3. :pseudo
    // 4. &

    // The generated declaration block eg body of the css rule
    let declarations = ''

    // Additional presedence values

    // this ensures that 'border-top-width' has a higer presedence than 'border-top'
    let maxPropertyPresedence = 0

    // more specfic utilities have less declarations and a higher presedence
    let numberOfDeclarations = 0

    // Walk through the object
    Object.keys(css).forEach((key) => {
      let value = css[key]

      if (is.function(value)) {
        value = value(context)
      }

      // string, number or Array => a property with a value
      if (includes('rg', (typeof value)[5]) || Array.isArray(value)) {
        // It is a Property
        const property = hyphenate(key)

        // Update presedence
        numberOfDeclarations += 1
        maxPropertyPresedence = Math.max(
          maxPropertyPresedence,
          declarationPropertyPrecedence(property),
        )

        // Add to the declaration block with prefixer applied
        declarations =
          (declarations && declarations + ';') +
          stringifyDeclaration(property, value as string | number | string[], important)
      } else if (value) {
        // If the value is an object this must be a nested block
        // like '@media ...', '@supports ... ', ':pseudo ...', '& > ...'
        // If this is a `@` rule
        if (key[0] === '@') {
          if (key[1] === 'f') {
            // `@font-face` is never wrapped, eg always global/root
            stringify([], key, 0, value as CSSRules, important)
          } else if (key[1] === 'k') {
            // @keyframes handling
            // To prevent
            // "@keyframes spin{from{transform:rotate(0deg)}}"
            // "@keyframes spin{to{transform:rotate(360deg)}}"
            // we generate waypoints without prefix and grap them from the stack
            // "from{transform:rotate(0deg)}"
            // "to{transform:rotate(360deg)}"
            // => "@keyframes name{from{transform:rotate(0deg)}from{transform:rotate(0deg)}}"
            const currentSize = rules.length

            stringify([], '', 0, value as CSSRules, important)

            const waypoints = rules.splice(currentSize, rules.length - currentSize)

            // Insert without wrappers
            rules.push({
              r: stringifyBlock(
                join(
                  waypoints.map((p) => p.r),
                  '',
                ),
                key,
              ),
              p: waypoints.reduce((sum, p) => sum + p.p, 0),
            })
          } else {
            // Some nested block like @media, dive into it
            stringify(
              [...atRules, key],
              selector,
              presedence | (responsivePrecedence(key) || atRulePresedence(key)),
              value as CSSRules,
              important,
            )
          }
        } else {
          // A selector block: { '&:focus': { ... } }
          stringify(
            atRules,
            // If this is a nested selector we need to
            // - replace `&` with the current selector
            selector
              ? // Go over the selector and replace the matching selectors respecting multiple selectors
                selector.replace(/([^,])+/g, (selectorPart) =>
                  // Same for the key
                  key.replace(/([^,])+/g, (keyPart) =>
                    // If the current part has a nested selector replace it
                    includes(keyPart, '&')
                      ? keyPart.replace(/&/g, selectorPart)
                      : (selectorPart && selectorPart + ' ') + keyPart,
                  ),
                )
              : key,
            // - propagate the presedence; if it is not nested we reset the presedence as it is most likely a global styles
            selector && includes(key, '&') ? presedence : 0,
            value as CSSRules,
            important,
          )
        }
      }
    })

    // We have collected all properties
    // if there have been some we need to create a css rule
    if (numberOfDeclarations) {
      // Inject declarations

      rules.push({
        // Wrap block with upper at-rules
        r: atRules.reduceRight(stringifyBlock, stringifyBlock(declarations, selector)),

        // Calculate precedence
        p:
          presedence *
            // Declarations: 8 bits = 256
            (1 << 8) +
          // 4: greatest precedence of properties
          // if there is no property presedence this is most likely a custom property only declaration
          // these have the highest presedence
          ((((maxPropertyPresedence || 15) & 15) << 4) |
            // 4: number of declarations (descending)
            (Math.max(0, 15 - numberOfDeclarations) & 15)),
      })
    }
  }

  const variantPresedence = makeVariantPresedenceCalculator(theme, variants)

  return (css, className, rule) => {
    rules = []

    stringify(
      [],
      className ? '.' + escape(className) : '',
      // If we have a rule, create starting presedence based on the variants
      rule ? rule.v.reduceRight(variantPresedence, 0) : 0,
      css,
      rule && rule.i,
    )

    return rules
  }
}
import type { Context, Sheet, Mode, SheetInit } from '../types'

import { sortedInsertionIndex } from './util'

import type { RuleWithPresedence } from './serialize'

// Insert css rules using presedence to find the correct position within the sheet
export const inject = (
  sheet: Sheet,
  mode: Mode,
  init: SheetInit,
  context: Context,
): ((rule: RuleWithPresedence) => void) => {
  // An array of presedence by index within the sheet
  // always sorted
  let sortedPrecedences: number[]
  init<number[]>((value = []) => (sortedPrecedences = value))

  // Cache for already inserted css rules
  // to prevent double insertions
  let insertedRules: Set<string>
  init<Set<string>>((value = new Set()) => (insertedRules = value))

  return ({ r: css, p: presedence }) => {
    // If not already inserted
    if (!insertedRules.has(css)) {
      // Mark rule as inserted
      insertedRules.add(css)

      // Find the correct position
      const index = sortedInsertionIndex(sortedPrecedences, presedence)

      try {
        // Insert
        sheet.insert(css, index)

        // Update sorted index
        sortedPrecedences.splice(index, 0, presedence)
      } catch (error) {
        // Some thrown error a because of specific pseudo classes
        // let filter them to prevent unnecessary warnings
        // ::-moz-focus-inner
        // :-moz-focusring
        if (!/:-[mwo]/.test(css)) {
          mode.report({ id: 'INJECT_CSS_ERROR', css, error: error as Error }, context)
        }
      }
    }
  }
}
import type {
  Configuration,
  Context,
  Preflight,
  ThemeResolver,
  Theme,
  Rule,
  Hasher,
  InlineDirective,
  CSSRules,
  Mode,
} from '../types'

import { corePlugins } from './plugins'
import { createPreflight } from './preflight'
import { coreVariants } from './variants'

import { cssomSheet, voidSheet } from './sheets'
import { silent, strict, warn } from './modes'
import { autoprefix, noprefix } from './prefix'
import { makeThemeResolver } from './theme'

import * as is from '../internal/is'
import { cyrb32, identity, join, tail } from './util'

import { parse } from './parse'
import { translate as makeTranslate } from './translate'
import { decorate as makeDecorate } from './decorate'
import { serialize as makeSerialize } from './serialize'
import { inject as makeInject } from './inject'

const sanitize = <T>(
  value: T | boolean | undefined,
  defaultValue: T,
  disabled: T,
  enabled = defaultValue,
): T => (value === false ? disabled : value === true ? enabled : value || defaultValue)

const loadMode = (mode: Configuration['mode']): Mode =>
  (is.string(mode) ? ({ t: strict, a: warn, i: silent } as Record<string, Mode>)[mode[1]] : mode) ||
  warn

// Creates rule id including variants, negate and directive
// which is exactly like a tailwind rule
const toString = (rule: Rule, directive = rule.d): string => {
  if (is.function(directive)) return ''

  const base = join(rule.v, '')

  return (base && tail(base) + ':') + (rule.n ? '-' : '') + directive + (rule.i ? '!' : '')
}

export const configure = (
  config: Configuration = {},
): { init: () => void; process: (tokens: unknown[]) => string; theme: ThemeResolver } => {
  const theme = makeThemeResolver(config.theme)

  const mode = loadMode(config.mode)

  const hash = sanitize<Hasher | false>(config.hash, false, false, cyrb32)

  // Track the active rule
  // 1. to detect if a theme value should be negated
  // 2. for nested `tw()` calls
  //    `sm:hover:${({ tw }) => tw`underline`}`
  //    ==> 'sm:hover:underline`
  // Start with an "empty" rule, to always have value to use
  let activeRule: { v: string[]; n?: boolean } = { v: [] }

  // The context that is passed to functions to access the theme, ...
  const context: Context = {
    // eslint-disable-next-line @typescript-eslint/no-use-before-define
    tw: (...tokens: unknown[]) => process(tokens),

    theme: ((section: keyof Theme, key?: string | string[], defaultValue?: unknown): unknown => {
      // Empty key us ethe standard tailwind default key
      if (key != null && !key.length) {
        key = 'DEFAULT'
      }

      // If no theme value is found, notify 'mode', it may be able to resolve a value
      const value =
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        theme(section, key as string, defaultValue as any) ||
        mode.unknown(
          section,
          key == null || Array.isArray(key) ? key : key.split('.'),
          defaultValue != null,
          context,
        )

      // Add negate to theme value using calc to support complex values
      return activeRule.n && value && is.string(value) ? `calc(${value} * -1)` : value
    }) as ThemeResolver,

    tag: (value) => (hash ? hash(value) : value),
  }

  // Used to translate a rule using plugins
  const translate = makeTranslate({ ...corePlugins, ...config.plugins }, context)

  // Wrap `translate()` to keep track of the active rule
  // we need to use try-finally as mode.report may throw
  // and we must always reset the active rule
  const tryTranslate = (rule: Rule): ReturnType<typeof translate> => {
    // Keep track of active variants for nested `tw` calls
    const parentRule = activeRule
    activeRule = rule

    try {
      return translate(rule)
    } finally {
      activeRule = parentRule
    }
  }

  const variants = { ...coreVariants, ...config.variants }

  // Apply variants to a translation
  const decorate = makeDecorate(config.darkMode || 'media', variants, context)

  // Serialize a translation to css
  const serialize = makeSerialize(sanitize(config.prefix, autoprefix, noprefix), variants, context)

  const sheet = config.sheet || (typeof window === 'undefined' ? voidSheet() : cssomSheet(config))

  const { init = (callback) => callback() } = sheet

  // Inject css into the target enviroment
  const inject = makeInject(sheet, mode, init, context)

  // Cache rule ids and their generated class name
  let idToClassName: Map<string, string>
  init<Map<string, string>>((value = new Map()) => (idToClassName = value))

  // Cache generated inline directive names by their function identity
  const inlineDirectiveName = new WeakMap<InlineDirective, string>()

  // Used as replacer for JSON.stringify to calculate the hash for a inline function
  const evaluateFunctions = (key: string, value: unknown): unknown =>
    is.function(value) ? value(context) : value

  // Responsible for converting (translate, decorate, serialize, inject) a rule
  const convert = (rule: Rule): string | undefined | void => {
    // If there is a active rule this one is nested
    // we must add the variants and need to reset the id
    if (activeRule.v.length) {
      rule = { ...rule, v: [...activeRule.v, ...rule.v], $: '' }
    }

    // Static rules (from template literals) can cache their id
    // this greatly improves performance
    if (!rule.$) {
      // For inline directives (functions) `toString` returns an empty string
      // in that case we check if we already have a name for the function
      // and use that one to generate the id
      rule.$ = toString(rule) || toString(rule, inlineDirectiveName.get(rule.d as InlineDirective))
    }

    // Check if we already have a class name for this rule id
    let className = idToClassName.get(rule.$)

    // We check for nullish because we put an empty string into `idToClassName`
    // if a rule did not generate a class name
    // This way we report the unknown directives only once
    if (className == null) {
      // 2. translate each rule using plugins
      let translation = tryTranslate(rule)

      // If this is a unknown inline directive
      if (!rule.$) {
        // We can now generate a unique name based on the created translation
        // This id does not include the variants as a directive is always independent of variants
        // Variants are applied below using `decorate()`
        // JSON.stringify ignores functions - by using a custom replace
        // we can calculate a hash based on the value returned by these functions
        rule.$ = cyrb32(JSON.stringify(translation, evaluateFunctions))

        // Remember it
        inlineDirectiveName.set(rule.d as InlineDirective, rule.$)

        // Generate an id including the current variants
        rule.$ = toString(rule, rule.$)
      }

      // CSS class names have been returned
      if (is.string(translation)) {
        // Use as is
        className = translation
      } else if (is.object(translation)) {
        // 3. decorate: apply variants
        translation = decorate(translation, rule)

        className = hash ? hash(JSON.stringify(translation, evaluateFunctions)) : rule.$

        // 4. serialize: convert to css string with precedence
        // 5. inject: add to dom
        serialize(translation, className, rule).forEach(inject)
      } else {
        // No plugin or plugin did not return something
        className = rule.$
        mode.report({ id: 'UNKNOWN_DIRECTIVE', rule: className }, context)
      }

      // Remember the generated class name
      idToClassName.set(rule.$, className)

      // Ensure the cache does not grow unlimited
      if (idToClassName.size > 30000) {
        idToClassName.delete(idToClassName.keys().next().value)
      }
    }

    return className
  }

  // This function is called from `tw(...)`
  // it parses, translates, decorates, serializes and injects the tokens
  const process = (tokens: unknown[]): string =>
    parse(tokens).map(convert).filter(Boolean).join(' ')

  // Determine if we should inject the preflight (browser normalize)
  const preflight = sanitize<Preflight | false | CSSRules>(config.preflight, identity, false)

  if (preflight) {
    // Create the base tailwind preflight css rules
    const css = createPreflight(theme)

    // Call the preflight handler, serialize and inject the result
    const styles = serialize(
      is.function(preflight) ? preflight(css, context) || css : { ...css, ...preflight },
    )

    init<boolean>((injected = (styles.forEach(inject), true)) => injected)
  }

  return {
    init: () => mode.report({ id: 'LATE_SETUP_CALL' }, context),
    process,
    theme,
  }
}
import type { Configuration, Instance, ThemeResolver, Theme } from '../types'

import { configure } from './configure'

export const create = (config?: Configuration): Instance => {
  // We are using lazy variables to trigger setup either
  // on first `setup` or `tw` call
  //
  // This allows to provide one-time lazy configuration
  //
  // These variables are not named `tw` and `setup`
  // as we use `tw` and `setup` to find the callee site
  // during stacktrace generation
  // This allows the error stacktrace to start at the call site.

  // Used by `tw`
  let process = (tokens: unknown[]): string => {
    // eslint-disable-next-line @typescript-eslint/no-use-before-define
    init()
    return process(tokens)
  }

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  let theme = ((section: keyof Theme, key?: string | string[], defaultValue?: any) => {
    init()
    return theme(section, key as string, defaultValue)
  }) as ThemeResolver

  // Used by `setup`
  let init = (config?: Configuration): void => {
    // Replace implementation with configured ones
    // `process`: the real one
    // `init`: invokes `mode.report` with `LATE_SETUP_CALL`
    // eslint-disable-next-line @typescript-eslint/no-extra-semi
    ;({ process, init, theme } = configure(config))
  }

  // If we got a config, start right away
  if (config) init(config)

  // The instance methods delegate to the lazy ones.
  // This ensures that after setup we use the configured
  // `process` and `setup` fails.
  return {
    tw: (...tokens: unknown[]) => process(tokens),

    setup: (config) => init(config),

    theme,
  }
}
import { create } from './twind/instance'

const instance = create()

export const { tw } = instance
export const { setup } = instance
export const { theme } = instance

